import React, { Component } from 'react';

class SearchMenu extends Component {
  constructor(props) {
      super(props);
      this.state = {
          nameFilter : props.name,
      }
      console.log(props);
  }

  render () {
    const { nameFilter } = this.state;
    return (
      <div>
        <input
          value={this.props.name}
          onChange={this.props.changeNameFilter}
        />
      </div>
    );
  }
}

export default SearchMenu;
