import Rolodex from './components/Rolodex';
import Title from './components/Title';
import SearchMenu from './components/SearchMenu';
import './App.css';
import { useState, useEffect } from 'react';

const App = () => {
  const title = 'Monster Rolodex';
  const [nameFilter, setNameFilter] = useState('');
  const [monsters, setMonsters] = useState([]);
  const [filteredMonsters, setFilteredMonsters] = useState([]);

  console.log('render');
  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(response => response.json())
    .then(data => {
      setMonsters(data);
      setFilteredMonsters(data);
    });
  }, []);

  useEffect(() => {
    if(filter === '')
      setFilteredMonsters(monsters);
    else{
      let newMonsters = monsters.filter( monster => monster.name.toLowerCase().includes(filter.toLowerCase()));
      setFilteredMonsters(newMonsters);
    }
  }, [monsters, nameFilter]);

  const changeNameFilter = (e) => {
    let newFilter = e.target.value;
    setNameFilter(newFilter);
  }

  return (
    <div className="App">
      <Title
        title={title}
      />
      <SearchMenu
        name={nameFilter}
        onNameChange={changeNameFilter}
      />
      <Rolodex
        data={filteredMonsters}
      />
    </div>
  );
}

export default App;
