import React, { Component } from 'react';

class Rolodex extends Component {
  constructor(props) {
      super(props);
      this.state = {
          monsters : [],
      }
  }

  render () {
    const { monsters } = this.state;
    return (
      <div>
        { monsters.map( monster => <div key={monster.id}>{monster.name}</div>) }
      </div>
    );
  }
}

export default Rolodex;
