import React, { Component } from 'react';

class SearchMenu extends Component {
  constructor(props) {
      super(props);
      this.state = {
          nameFilter : '',
      }
  }

  changeFilter = (e, i) => {
    console.log(e, i);
    this.setState({ nameFilter : e.value });
  }

  render () {
    const { nameFilter } = this.state;
    return (
      <div>
        <input
          value={nameFilter}
          onChange={this.changeFilter}
        />
      </div>
    );
  }
}

export default SearchMenu;
