import React, { Component } from 'react';

class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: props.image,
      title: props.title,
      text: props.text,
    }
  }

  render () {
    const { image, title, text } = this.state;
    return (
      <div className={'card-container'}>
        <img className={'card-image'} src={`https://robohash.org/${image}?set=set${image}`} width={'300'} height={'300'}/>
        <h3 className={'card-title'}>{title}</h3>
        <p className={'card-text'}>{text}</p>
      </div>
    );
  }
}

export default Card;
